DROP TABLE IF EXISTS `packages`;
CREATE TABLE `packages` (
  `package_id` int(11) NOT NULL AUTO_INCREMENT,
  `package_created` datetime NOT NULL,
  `package_updated` datetime NOT NULL,
  `package_creatorid` int(11) DEFAULT NULL,
  `package_name` varchar(150) DEFAULT NULL,
  `package_subscription_options` varchar(150) DEFAULT 'paid' COMMENT 'free|paid',
  `package_amount_monthly` decimal(10,2) DEFAULT 0.00,
  `package_amount_yearly` decimal(10,2) DEFAULT 0.00,
  `package_gateway_stripe_product_monthly` varchar(150) DEFAULT NULL COMMENT 'API Info - Product is the parent to a plan',
  `package_gateway_stripe_price_monthly` varchar(150) DEFAULT NULL COMMENT 'API Info - This is called "price:',
  `package_gateway_stripe_product_yearly` varchar(150) DEFAULT NULL COMMENT 'API Info - Product is the parent to a plan',
  `package_gateway_stripe_price_yearly` varchar(150) DEFAULT NULL COMMENT 'API Info - This is called "price:',
  `package_gateway_paypal_plan_monthly` varchar(150) DEFAULT NULL,
  `package_gateway_paypal_plan_yearly` varchar(150) DEFAULT NULL,
  `package_gateway_razorpay_plan_monthly` varchar(150) DEFAULT NULL,
  `package_gateway_razorpay_plan_yearly` varchar(150) DEFAULT NULL,
  `package_gateway_paystack_plan_monthly` varchar(150) DEFAULT NULL,
  `package_gateway_paystack_plan_yearly` varchar(150) DEFAULT NULL,
  `package_description` text DEFAULT NULL,
  `package_icon` varchar(100) DEFAULT NULL,
  `package_featured` varchar(10) DEFAULT 'no' COMMENT 'yes|no',
  `package_limits_clients` int(11) DEFAULT 0 COMMENT '-1 is unlimited',
  `package_limits_team` int(11) DEFAULT 0 COMMENT '-1 is unlimited',
  `package_limits_projects` int(11) DEFAULT 0 COMMENT '-1 is unlimited',
  `package_module_projects` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_tasks` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_invoices` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_leads` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_knowledgebase` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_estimates` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_expense` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_subscriptions` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_tickets` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_timetracking` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_reminders` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_proposals` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_contracts` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_messages` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_module_calendar` varchar(10) DEFAULT 'yes' COMMENT 'yes|no',
  `package_status` varchar(10) DEFAULT 'active' COMMENT 'active|archived',
  `package_visibility` varchar(20) DEFAULT 'visible' COMMENT 'visible|hidden',
  `package_sync_date` datetime DEFAULT NULL,
  `package_sync_status` varchar(40) DEFAULT 'synced' COMMENT 'awaiting-sync|syncing|synced',
  PRIMARY KEY (`package_id`),
  KEY `package_creatorid` (`package_creatorid`),
  KEY `package_featured` (`package_featured`),
  KEY `package_status` (`package_status`),
  KEY `package_visibility` (`package_visibility`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='[truncate]';


DROP TABLE IF EXISTS `pages`;
CREATE TABLE `pages` (
  `page_id` int(11) NOT NULL AUTO_INCREMENT,
  `page_uniqueid` varchar(100) DEFAULT NULL,
  `page_created` datetime NOT NULL,
  `page_updated` datetime NOT NULL,
  `page_creatorid` int(11) NOT NULL,
  `page_title` varchar(250) DEFAULT NULL,
  `page_permanent_link` varchar(250) DEFAULT NULL,
  `page_content` text DEFAULT NULL,
  `page_meta_title` varchar(250) DEFAULT NULL,
  `page_meta_description` varchar(250) DEFAULT NULL,
  `page_show_title` varchar(50) DEFAULT 'yes' COMMENT 'yes|no',
  `page_status` varchar(100) DEFAULT 'draft' COMMENT 'draft|published',
  PRIMARY KEY (`page_id`),
  KEY `page_permanent_link` (`page_permanent_link`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `payments`;
CREATE TABLE `payments` (
  `payment_id` int(11) NOT NULL AUTO_INCREMENT,
  `payment_created` datetime NOT NULL,
  `payment_updated` datetime NOT NULL,
  `payment_date` datetime DEFAULT NULL,
  `payment_subscription_id` int(11) DEFAULT NULL,
  `payment_transaction_id` varchar(100) DEFAULT NULL,
  `payment_tenant_id` int(11) NOT NULL,
  `payment_gateway` varchar(100) DEFAULT NULL,
  `payment_amount` decimal(10,2) DEFAULT NULL,
  PRIMARY KEY (`payment_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='[truncate]';


DROP TABLE IF EXISTS `payment_sessions`;
CREATE TABLE `payment_sessions` (
  `session_id` int(11) NOT NULL AUTO_INCREMENT,
  `session_created` datetime DEFAULT NULL,
  `session_updated` datetime DEFAULT NULL,
  `session_creatorid` int(11) DEFAULT NULL COMMENT 'user making the payment',
  `session_creator_fullname` varchar(150) DEFAULT NULL,
  `session_creator_email` varchar(150) DEFAULT NULL,
  `session_gateway_name` varchar(150) DEFAULT NULL COMMENT 'stripe | paypal | etc',
  `session_gateway_ref` varchar(150) DEFAULT NULL COMMENT 'the checkout_session_id (either from the gateway or system generated)',
  `session_gateway_ref_2` varchar(150) DEFAULT NULL,
  `session_amount` decimal(10,2) DEFAULT NULL COMMENT 'amount of the payment',
  `session_invoices` varchar(250) DEFAULT NULL COMMENT '[currently] - single invoice id | [future] - comma seperated list of invoice id''s that are for this payment',
  `session_subscription_id` int(11) DEFAULT NULL COMMENT 'subscription id',
  `session_payload` text DEFAULT NULL,
  `session_status` varchar(20) DEFAULT 'pending' COMMENT 'pending|completed',
  PRIMARY KEY (`session_id`),
  KEY `session_gateway_name` (`session_gateway_name`),
  KEY `session_gateway_ref` (`session_gateway_ref`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='[truncate]';


DROP TABLE IF EXISTS `proof_of_payments`;
CREATE TABLE `proof_of_payments` (
  `proof_id` int(11) NOT NULL AUTO_INCREMENT,
  `proof_created` datetime NOT NULL,
  `proof_updated` datetime NOT NULL,
  `proof_tenant_id` int(11) NOT NULL,
  `proof_amount` decimal(20,2) DEFAULT NULL,
  `proof_date` date DEFAULT NULL,
  `proof_directory` varchar(200) DEFAULT NULL,
  `proof_filename` varchar(200) DEFAULT NULL,
  `proof_status` varchar(30) DEFAULT 'unread' COMMENT 'unread|read',
  PRIMARY KEY (`proof_id`),
  KEY `proof_tenant_id` (`proof_tenant_id`),
  KEY `proof_status` (`proof_status`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `scheduled`;
CREATE TABLE `scheduled` (
  `scheduled_id` int(11) NOT NULL AUTO_INCREMENT,
  `scheduled_created` datetime NOT NULL,
  `scheduled_status` varchar(100) DEFAULT 'new' COMMENT 'new|processing|failed|completed',
  `scheduled_updated` datetime NOT NULL,
  `scheduled_gateway` varchar(100) DEFAULT NULL COMMENT 'stripe|paypal | etc| all-gateways',
  `scheduled_type` varchar(100) DEFAULT NULL COMMENT 'delete-database | cancel-subscription | delete-plan | update-plan-name |  update-plan-price',
  `scheduled_payload_1` varchar(100) DEFAULT '' COMMENT 'subscription id | plan id | etc',
  `scheduled_payload_2` text DEFAULT '' COMMENT 'optional data',
  `scheduled_payload_3` text DEFAULT NULL,
  `scheduled_payload_4` text DEFAULT NULL,
  `scheduled_payload_5` text DEFAULT NULL,
  `scheduled_attempts` int(11) DEFAULT 0,
  `scheduled_manual_action_required` varchar(100) DEFAULT 'no' COMMENT 'yes|no (can use this in the future to show admin an action log)',
  `scheduled_comments` text DEFAULT NULL,
  PRIMARY KEY (`scheduled_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='[truncate] - schedule of susbscriptions to be cancelled at payment gateway via cron';


DROP TABLE IF EXISTS `sessions`;
