DROP TABLE IF EXISTS `subscriptions`;
CREATE TABLE `subscriptions` (
  `subscription_id` int(11) NOT NULL AUTO_INCREMENT,
  `subscription_created` datetime DEFAULT NULL,
  `subscription_gateway_id` varchar(100) DEFAULT NULL COMMENT 'from gateway',
  `subscription_status` varchar(100) DEFAULT 'awaiting-payment' COMMENT 'free-trial | awaiting-payment | active | failed | cancelled',
  `subscription_customerid` int(11) DEFAULT NULL,
  `subscription_uniqueid` varchar(200) DEFAULT NULL,
  `subscription_updated` datetime DEFAULT NULL,
  `subscription_creatorid` int(11) DEFAULT NULL,
  `subscription_type` varchar(30) DEFAULT 'paid' COMMENT 'paid|free',
  `subscription_payment_method` varchar(50) DEFAULT 'automatic' COMMENT 'automatic|offline',
  `subscription_amount` decimal(10,2) DEFAULT NULL,
  `subscription_trial_end` date DEFAULT NULL,
  `subscription_date_started` date DEFAULT NULL,
  `subscription_date_renewed` date DEFAULT NULL COMMENT 'from gateway',
  `subscription_date_next_renewal` date DEFAULT NULL COMMENT 'from gateway',
  `subscription_package_id` int(11) DEFAULT NULL COMMENT 'the planid, when subscription was created',
  `subscription_gateway_name` varchar(50) DEFAULT NULL COMMENT 'stripe|paypal',
  `subscription_gateway_plan_id` varchar(100) DEFAULT NULL COMMENT '[stripe = product] [paypal = foo]',
  `subscription_gateway_price_id` varchar(100) DEFAULT NULL COMMENT '[stripe = price] [paypal = null]',
  `subscription_gateway_billing_cycle` varchar(100) DEFAULT NULL COMMENT 'monthly|yearly',
  `subscription_gateway_last_message` text DEFAULT NULL COMMENT '(optional) from gateway',
  `subscription_gateway_last_date` date DEFAULT NULL COMMENT '(optional) from gateway',
  `subscription_checkout_reference` varchar(200) DEFAULT NULL COMMENT '(optional) any additional data',
  `subscription_checkout_reference_2` varchar(200) DEFAULT NULL COMMENT '(optional) any additional data',
  `subscription_checkout_reference_3` varchar(200) DEFAULT NULL COMMENT '(optional) any additional data',
  `subscription_checkout_reference_4` varchar(200) DEFAULT NULL COMMENT '(optional) any additional data',
  `subscription_checkout_reference_5` varchar(200) DEFAULT NULL COMMENT '(optional) any additional data',
  `subscription_checkout_payload` text DEFAULT NULL COMMENT '(optional) any additional data',
  `subscription_archived` varchar(20) DEFAULT 'no' COMMENT 'yes|no',
  PRIMARY KEY (`subscription_id`),
  KEY `subscription_creatorid` (`subscription_creatorid`),
  KEY `subscription_clientid` (`subscription_customerid`),
  KEY `subscription_status` (`subscription_status`),
  KEY `subscription_type` (`subscription_type`),
  KEY `subscription_package_id` (`subscription_package_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='[truncate]';


DROP TABLE IF EXISTS `tenants`;
CREATE TABLE `tenants` (
  `tenant_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `domain` varchar(255) DEFAULT NULL,
  `subdomain` varchar(255) DEFAULT NULL,
  `domain_type` varchar(255) DEFAULT 'subdomain' COMMENT 'subdomain|custom',
  `database` varchar(150) DEFAULT NULL,
  `tenant_created` timestamp NULL DEFAULT NULL,
  `tenant_updated` timestamp NULL DEFAULT NULL,
  `tenant_creatorid` int(11) DEFAULT NULL,
  `tenant_name` varchar(50) DEFAULT NULL,
  `tenant_email` varchar(150) DEFAULT NULL,
  `tenant_stripe_customer_id` varchar(150) DEFAULT NULL COMMENT 'used for stripe checkout',
  `tenant_paystack_customer_id` varchar(150) DEFAULT NULL COMMENT 'used for paystack checkout',
  `tenant_razorpay_customer_id` varchar(150) DEFAULT NULL COMMENT 'used for razorpay checkout',
  `tenant_status` varchar(150) DEFAULT 'unsubscribed' COMMENT 'unsubscribed|free-trial|awaiting-payment|failed|active|cancelled',
  `tenant_password` varchar(150) DEFAULT NULL,
  `tenant_last_synced` date DEFAULT NULL,
  `tenant_email_config_type` varchar(100) DEFAULT 'local' COMMENT 'local|smtp',
  `tenant_email_config_status` varchar(100) DEFAULT 'pending',
  `tenant_email_local_email` varchar(200) DEFAULT NULL,
  `tenant_email_forwarding_email` varchar(200) DEFAULT NULL,
  `tenant_sync_status` varchar(40) DEFAULT NULL COMMENT 'awaiting-sync|null',
  `tenant_sync_user` varchar(40) DEFAULT NULL COMMENT 'awaiting-sync|null',
  `tenant_temp_settings_system_currency_code` text DEFAULT NULL,
  `tenant_temp_settings_system_currency_symbol` text DEFAULT NULL,
  `tenant_temp_settings_system_currency_position` text DEFAULT NULL,
  `tenant_updating_current_version` varchar(10) DEFAULT NULL,
  `tenant_updating_target_version` varchar(10) DEFAULT NULL,
  `tenant_updating_status` varchar(40) DEFAULT 'completed' COMMENT 'completed|failed',
  `tenant_updating_log` text DEFAULT NULL,
  PRIMARY KEY (`tenant_id`),
  UNIQUE KEY `tenants_domain_unique` (`domain`),
  UNIQUE KEY `tenants_database_unique` (`database`),
  KEY `tenant_email` (`tenant_email`),
  KEY `tenant_status` (`tenant_status`),
  KEY `tenant_stripe_customer_id` (`tenant_stripe_customer_id`),
  KEY `tenant_updated` (`tenant_updated`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='[truncate]';


DROP TABLE IF EXISTS `updates`;
CREATE TABLE `updates` (
  `update_id` int(11) NOT NULL AUTO_INCREMENT,
  `update_created` datetime NOT NULL,
  `update_updated` datetime NOT NULL,
  `update_type` varchar(100) DEFAULT NULL COMMENT 'landlord|tenant',
